'use strict';
app.factory('RelatedAttributeSetDataService', ['$rootScope', 'CommonService', 'BaseService',
    function ($rootScope, commonService, baseService) {
        var serviceFactory = angular.copy(baseService);
        serviceFactory.init('related-attribute-data/portal');

        var _getList = async function (viewType, request,attributeSetId,parentType, parentId) {
            request.query  = '';        
            request.key = viewType;    
            if(parentType){
                if(request.query){
                    request.query += '&';
                }
                request.query += 'parentType=' + parentType;
            }
            if(parentId){
                if(request.query){
                    request.query += '&';
                }
                request.query += "parentId='" + parentId+ "'";
            }        
            if(attributeSetId){
                if(request.query){
                    request.query += '&';
                }
                request.query += "attributeSetId=" + attributeSetId;
            }        
            var url = this.prefixUrl + '/list';
           
            var req = {
                method: 'POST',
                url: url,
                data: JSON.stringify(request)
            };
            return await commonService.getApiResult(req);
        };
        
        serviceFactory.getList = _getList;
        return serviceFactory;

    }]);


'use strict';
app.factory('RelatedAttributeSetService', ['$rootScope', 'CommonService', 'BaseService',
    function ($rootScope, commonService, baseService) {
        var serviceFactory = angular.copy(baseService);
        serviceFactory.init('related-attribute-set/portal');
        
        var _getOthers = async function (viewType, objData, parentType, parentId) {
            objData.filter  = '';            
            if(parentType){
                if(objData.filter){
                    objData.filter += ' and ';
                }
                objData.filter += 'parentType eq ' + parentType;
            }
            if(parentId){
                if(objData.filter){
                    objData.filter += ' and ';
                }
                objData.filter += "parentId ne " + parentId;
            }        
            var data = serviceFactory.parseODataQuery(objData);           
            var url = this.prefixUrl + '/' + viewType;
            if(data){
                url = url.concat(data);
            }
            var req = {
                method: 'GET',
                url: url
            };
            return await commonService.getApiResult(req);
        };
        
        serviceFactory.getOthers = _getOthers;
        return serviceFactory;

    }]);

'use strict';
app.controller('AppSettingsController', 
    ['$scope', '$rootScope', 'ngAppSettings', '$routeParams', '$timeout', '$location', 
'AuthService','CommonService', 'AppSettingsServices',
    function ($scope, $rootScope, ngAppSettings, $routeParams, $timeout, $location, authService, commonService, appSettingsServices) {

        $scope.appSettings = null;
        $scope.errors = [];
        $scope.statuses = ngAppSettings.contentStatuses;
        $scope.getAppSettings = async function (id) {
            $rootScope.isBusy = true;
            var resp = await appSettingsServices.getAppSettings();
            if (resp && resp.isSucceed) {
                $scope.appSettings = JSON.stringify(resp.data);
                $rootScope.initEditor();
                $rootScope.isBusy = false;
                $scope.$apply();
            }
            else {
                if (resp) { $rootScope.showErrors(resp.errors); }
                $rootScope.isBusy = false;
                $scope.$apply();
            }
        };
        $scope.loadAppSettings = async function () {
            $rootScope.isBusy = true;

            var id = $routeParams.id;
            var response = await appSettingsServices.getAppSettings();
            if (response.isSucceed) {
                $scope.appSettings = response.data;
                $rootScope.isBusy = false;
                $scope.$apply();
            }
            else {
                $rootScope.showErrors(response.errors);
                $rootScope.isBusy = false;
                $scope.$apply();
            }

            var result = await commonService.getSettings();
            if (result.isSucceed) {
                $scope.settings = result.data;
                $rootScope.isBusy = false;
                $scope.$apply();
            }
            else {
                $rootScope.isBusy = false;
            }

            // load portal menus
            commonService.loadJArrayData('portal-menus.json').then(resp=>{
                $scope.menus = resp.data;
                $rootScope.isBusy = false;
                $scope.$apply();
            });
        };

        $scope.saveAppSettings = async function (appSettings) {
            $rootScope.isBusy = true;
            var resp = await appSettingsServices.saveAppSettings(appSettings);
            if (resp && resp.isSucceed) {
                $scope.appSettings = resp.data;
                $rootScope.showMessage('success', 'success');
                $rootScope.isBusy = false;
                $scope.$apply();
            }
            else {
                if (resp) { $rootScope.showErrors(resp.errors); }
                $scope.$apply();
            }
        };

    }]);

