            if (folder) {
                $scope.request.key += ($scope.request.key !== '') ? 'wwwroot/' : 'wwwroot';
                $scope.request.key += folder;
            } else {
                $scope.request.key = $routeParams.folder ? $routeParams.folder : 'wwwroot';
            }
